import React from 'react'


//aca te traes por props el estado de tus games per Page=15, todo tu estado global con todos los videogames
//paginado ==> se va seteando currentPage a medida que (el numero de numero de pagina cambia )

function Paginado({gamesPerPage,allGames, paginado}) {
    const pageNumber = []

    //que redonde por arriba la division
    //en nuestro caso son 4--> 100/15
    for (let i= 0; i<= Math.round(allGames/gamesPerPage); i++){
         pageNumber.push(i+1)
    }
//entonces eb PageNumber te va a



  return (
    <nav>
        <ul>
            { pageNumber &&
            pageNumber.map(number =>(             
                <ul key={number}>
                {/* //la function paginado me trae el numero numero se pagina ==>setCurrentPage */}
                <a onClick={()=>paginado(number)}>{number}</a>
                </ul>
                ))
            }
        </ul>
    </nav>
  )
}

export default Paginado
