import React from 'react';
import {NavLink} from "react-router-dom";
import styles from "../components/VideogameCreate.module.css"
import { createVideogames,getGenres, getPlatforms } from '../actions';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const divStyle = {
  backgroundImage: `url("https://img.freepik.com/vector-gratis/fondo-transparente-videojuegos_6997-1230.jpg?w=2000")`,
  backgroundSize: "cover",
  position:"fixed",
  backgroundPosition: "100% 100%",
  width: "100vw",
  height: "100vh",
};


// esta function validate recibe un input (lo que escribimos dentro de cada input)
function validate (input){
  let errors = {};
  if (!input.name){
    errors.name = "Se requiere un Nombre"
  }
  else if (!input.description){
    errors.description = "Se requiere una Descripción"
  }
  else if (!input.platforms){
    alert("no")
    errors.platforms = "Se requiere una Plataformas"
  }
  else if (!input.genres){
    errors.genres = "Se requiere al menos un genero"
  }
  return errors
}


function VideogameCreate() {

  const dispatch = useDispatch()
  const history = useHistory()


  const allGenres = useSelector((state)=> state.genres)
  const allPlatforms = useSelector((state)=> state.platforms)


const ejemplo = (allPlatforms.map((el)=>el.platforms)).flatMap(x => x)
console.log(ejemplo)

  // console.log(ejemplo)
// const ej2 = ejemplo.flatMap(x => x)
// // console.log(ej2)

// const ej3= new Set(ej2)

// const ej4 = Array.from(ej3)
// console.log (ej4)


  const [errors, setErrors]= useState({})

  const [input, setInput] = useState({
    name : "",
    description :"",
    image: "",
    rating: 1,
    released: "",
    genres: [],
    platforms: []
  })
console.log(input)

  useEffect (()=>{
    dispatch(getGenres());
  },[dispatch])

  useEffect (()=>{
    dispatch(getPlatforms());
  },[dispatch])


  function handleSubmit(e){
    e.preventDefault()
    if (Object.values(errors).length > 0) {
      alert("Please complete the information required");
    } else if (
      !input.name &&
      !input.description &&
      // !input.rating &&
      !input.platforms &&
      !input.genres.length
    ) {
      alert("Please complete the form");}
    else {
      dispatch(createVideogames(input));
    alert("PERSONAJE CREADO CON EXITO")
    setInput({
    name : "",
    description :"",
    image: "",
    rating: 1,
    released: "",
    genres: [],
    platforms: []
    })
    // setErrors({})
    history.push("/home")
//para que vuelva a la pagina anterior

    }}

  function handleChange(e){
    e.preventDefault()
    //e.target.value = e.target.name
    setInput({
      ...input,
      [e.target.name]: e.target.value
    })
    setErrors(validate({
      ...input,
      [e.target.name] : e.target.value}))
}

function handleSelectGenre(e){
  setInput({
    ...input,
    genres: [...input.genres,e.target.value]


  })
}

function handleSelectPlatform(e){
  setInput({
    ...input,
    platforms: [...input.platforms, e.target.value]

  })
}

function handleDelete(el){
  // el.preventDefault()
  setInput({
    ...input,
    genres : input.genres.filter((t) => t !== el)

  })
}


  return (
    <div style={divStyle}>
   {/* <div className={styles.root}>  */}

  <NavLink to = "/home" exact >
      <button className={styles.btn}>
        HOME
      </button>
  </NavLink>
  <h1 className={styles.h1}> CREA TU VIDEOJUEGO</h1>

  <form className = {styles.form}  onSubmit={(e) => handleSubmit(e)}>
<div>
  <label>Nombre: </label>
<input

className={errors.name && styles.danger}
  type= "text"
  name= "name"
  value={input.name}
  onChange={handleChange}
  />
 {!errors.name ? null : <div className={styles.error}>{errors.name}</div>}
 </div>
<div>
  <label> Descripción: </label>
  <input
    className={errors.description && styles.danger}
    type="text"
    name="description"
    value={input.description}
    onChange={handleChange}
    />
   {!errors.description ? null : <p className={styles.error}>{errors.description}</p>}

  </div>
  <div>
  <label>Fecha de lanzamiento:</label>
  <input type="text"
    name="released"
    value={input.released}
    onChange={handleChange}
    />

  </div>
  <div>
  <label>
    Rating:</label>
    <input type="text"
    name="rating"
    value={input.rating}
    onChange={handleChange}/>
  </div>
  <div>
  <label>
    Imagen:</label>
    <input type="text"
    name="image"
    value={input.image}
    onChange={handleChange} />
  </div>

<div>
Elige Genero/s:
  <select onChange={handleSelectGenre}>
            <option value="" disabled={true} className={errors.genres && styles.danger}>Genres</option>

            {allGenres.map((t)=>(
              <option key={t.id} value={t.name}>{t.name}</option>
               ))}
  </select>

  </div>

  {input.genres.map((el, index) => (
            <div key={index}>
              {el}
              <button type="button" onClick={() => handleDelete(el)}>X</button>
            </div>
          ))}


{/* <div>

Elige Plataforma/s:
   <select onChange={handleSelectPlatform}>
    <option value="">Platforms</option>
      {ej4.map((t, index)=>(

<option key={index} value={t}>{t}</option>
))}
          </select>
</div> */}

<div className={styles.crear}>
   <input type="submit" value="Crear" disabled={!!errors.name || !!errors.description || !!errors.genres || !!errors.platforms}></input>
   </div>
  </form>



</div>
  )
}

export default VideogameCreate
