import React from 'react';
import {NavLink} from "react-router-dom";
import styles from "../components/VideogameCreate.module.css"
import { createVideogames,getGenres, getPlatforms } from '../actions';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const divStyle = {
  backgroundImage: `url("https://img.freepik.com/vector-gratis/fondo-transparente-videojuegos_6997-1230.jpg?w=2000")`,
  backgroundSize: "cover",
  position:"fixed",
  backgroundPosition: "100% 100%",
  width: "100vw",
  height: "100vh",
};


function VideogameCreate() {

  const dispatch = useDispatch()
  const history = useHistory()


  const allGenres = useSelector((state)=> state.genres)
  const allPlatforms = useSelector((state)=> state.platforms)


// esta function validate recibe un input (lo que escribimos dentro de cada input)
function validate (input){
  let errors = {};
  if (!input.name){
    errors.name = "Se requiere un Nombre"
  } 
  if (!input.description){
    errors.description = "Se requiere una DescripciÃ³n"
  }
  if (!input.platforms){
    errors.platforms = "Se requiere una Plataformas"
  }
  if (!input.genres){
    errors.genres = "Se requiere al menos un genero"
  }
  return errors
}


  const [errors, setErrors]= useState({})

  const [input, setInput] = useState({
    name : "",
    description :"",
    image: "",
    rating: 1,
    released: "",
    genres: [],
    platforms: []
  })


  useEffect (()=>{
    dispatch(getGenres());
  },[dispatch])

  useEffect (()=>{
    dispatch(getPlatforms());
  },[dispatch])


  function handleSubmit(e){
    e.preventDefault()
    dispatch(createVideogames(input))
    alert("PERSONAJE CREADO CON EXITO")
    setInput({
    name : "",
    description :"",
    image: "",
    rating: 1,
    released: "",
    genres: [],
    platforms: []
    })
    setErrors({})
    history.push("/home")
//para que vuelva a la pagina anterior

  }

  function handleChange(e){
    e.preventDefault()
    //e.target.value = e.target.name
    setInput({
      ...input,
      [e.target.name]: e.target.value
    })
    setErrors(validate({
      ...input, 
      [e.target.name] : e.target.value}))
}

function handleSelect(e){
  setInput({
    ...input,
    genres: [...input.genres,e.target.value],
    platforms: [...input.platforms, e.target.value]

  })
}


  return (
    <div style={divStyle}>
   {/* <div className={styles.root}>  */}

  <NavLink to = "/home" exact >
      <button className={styles.btn}>
        HOME
      </button>
  </NavLink>
  <h1 className={styles.h1}> CREA TU VIDEOJUEGO</h1>

  <form className = {styles.form} onSubmit={handleSubmit}>
<div>
  <label>Nombre: </label>
<input 
  type= "text"
  name= "name"
  value={input.name}
  onChange={handleChange}
  />
  {errors.name && (
    <p>{errors.name}</p>
  )}

</div>
<div>
  <label> DescripciÃ³n: </label>
  <input type="text" 
    name="description"
    value={input.description}
    onChange={handleChange}
    />
   {errors.description && (
    <p>{errors.description}</p>
  )}

  </div>
  <div>
  <label>Fecha de lanzamiento:</label>
  <input type="text" 
    name="released"
    value={input.released}
    onChange={handleChange}
    />

  </div>
  <div>
  <label>
    Rating:</label>
    <input type="text" 
    name="rating" 
    value={input.rating}
    onChange={handleChange}/>
  </div>
  <div>
  <label>
    Imagen:</label>
    <input type="text" 
    name="image"
    value={input.image}
    onChange={handleChange} />
  </div>

  <select onChange={handleSelect}>
            <option value="">Genres</option>
            {allGenres.map((t)=>(
              <option key={t.id} value={t.name}>{t.name}</option>
                 ))}
          </select>
  {errors.genres && (
    <p>{errors.genres}</p>
  )}

   <select onChange={handleSelect}>
    <option value="">Platforms</option>
      {allPlatforms.map((t)=>(

              <option key={t.id} value={t.platforms.map(t=>t)}>{t.platforms.map(t=>t)}</option>

                 ))}
          </select>  


   <input type="submit" value="Crear" disabled={!!errors.name || !!errors.description && aler }></input>
  </form>

</div>
  )
}

export default VideogameCreate
