import React from 'react'
import {NavLink, useParams} from "react-router-dom";
import styles from "../components/VideogameDetail.module.css"
import { getDatailVideogame } from '../actions';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';



function VideogameDetail(props) {

  const dispatch = useDispatch()


const allDetail = useSelector((state)=> state.detail)

  useEffect (()=>{  

    //esto es para acceder a los params(id) de la ruta
    dispatch(getDatailVideogame(props.match.params.id));
  },[dispatch])


  return (
    <div>{
      allDetail?
<div> 
<h5> {allDetail.name}</h5>
<img src={allDetail.createdInDb? allDetail.image :allDetail.image } alt="img not found"></img>

<p>{allDetail.description}</p>
<h1>{allDetail.genres}</h1>
<h1>{allDetail.platforms}</h1>
<h1>{allDetail.released}</h1>
<h1>{allDetail.rating}</h1>


</div>  : 
<h1>Loading..</h1>

}

<NavLink to="/home" exact>
  <button>Volver</button>
</NavLink>


      </div>


  )
}

export default VideogameDetail

