import React from 'react';
import {NavLink} from "react-router-dom";
import styles from "../components/VideogameCreate.module.css"
import { createVideogames,getGenres, getPlatforms } from '../actions';
import { useDispatch, useSelector } from 'react-redux';
import { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const divStyle = {
  backgroundImage: `url("https://img.freepik.com/vector-gratis/fondo-transparente-videojuegos_6997-1230.jpg?w=2000")`,
  backgroundSize: "cover",
  position:"fixed",
  backgroundPosition: "100% 100%",
  width: "100vw",
  height: "100vh",
};


function VideogameCreate() {

  const dispatch = useDispatch()
  const history = useHistory()

  const allGenres = useSelector((state)=> state.genres)
  const allPlatforms = useSelector((state)=> state.platforms)

  const [error, setError]= useState({})

  const [input, setInput] = useState({
    name : "",
    description :"",
    image: "",
    rating: 1,
    released: "",
    genres: [],
    platforms: []
  })

  useEffect (()=>{
    dispatch(getGenres());
  },[dispatch])

  useEffect (()=>{
    dispatch(getPlatforms());
  },[dispatch])

  function handleSubmit(e){
    e.preventDefault()
    dispatch(createVideogames(input))
    setInput({
    name : "",
    description :"",
    image: "",
    rating: 1,
    released: "",
    genres: [],
    platforms: []
    })
    history.push("/home")
//para que vuelva a la pagina anterior

  }

  function handleChange(e){
    e.preventDefault()
    e.target.value = e.target.name
    setInput({
    name : e.target.value,
    description : e.target.value,
    image: e.target.value,
    rating: 1,
    released: "",
    genres: [],
    platforms: []
    })

  }


  return (
    <div style={divStyle}>
   {/* <div className={styles.root}>  */}

  <NavLink to = "/home" exact >
      <button className={styles.btn}>
        HOME
      </button>
  </NavLink>
  <h1 className={styles.h1}> CREA TU VIDEOJUEGO</h1>

  <form className = {styles.form} onSubmit={handleSubmit}>
<div>
  <label>Nombre: </label>
<input 
  type= "text"
  name= "name"
  value={input.name}
  onChange={handleChange}
  />
  {/* {errors.name && (
    <p>{errors.name}</p>
  )} */}

</div>
<div>
  <label>
    Descripción:
  <input type="text" 
    name="description"
    value={input.description}
    onChange={handleChange}
    />
  </label>
  </div>
  <div>
  <label>
    Fecha de lanzamiento:
  <input type="text" 
    name="released"
    value={input.released}
    onChange={handleChange}
    />
  </label>
  </div>
  <div>
  <label>
    Rating:
    <input type="text" 
    name="rating" 
    value={input.rating}
    onChange={handleChange}/>
  </label>
  </div>
  <div>
  <label>
    Imagen:
    <input type="text" 
    name="image"
    value={input.image}
    onChange={handleChange} />
  </label>
  </div>
   <input type="submit" value="Crear"></input>
  </form>

</div>
  )
}

export default VideogameCreate
