import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { Link} from "react-router-dom";
import {getDetailVideogame} from "../actions/index";
import {styles} from'./VideogameDetails.css'


export default function Details(props) {

const dispatch = useDispatch()

const gameDetail = useSelector((state)=> state.detail)

  useEffect(() => {
    dispatch(getDetailVideogame(props.match.params.id));
    // return () => dispatch(Action.clearDetail());
  }, [dispatch]);

  return (
    <div className="container">
      <Link to="/home">
        <button className="btn-detail">Regresar al Home</button>
      </Link>
      <div className="sub-container">
      <h1 className="name">{gameDetail.name ? gameDetail.name : "No name found"}</h1>
      <img className="image"
        height="300px"
        width="500px"
        src={gameDetail.image ? gameDetail.image : "No img found"}
      />
      <label  className="genres-label" >Genres: </label>
      <h3 className="genres">

        {gameDetail.genres ? gameDetail.genres.map(e => e.name + (' /')) : "No genres found"}
      </h3>
      <label className="platform-label">Platforms: </label>
      <h3 className="platform-h3">

        {gameDetail.platforms
          ? gameDetail.platforms + "/ "
          : "No platforms found"}
      </h3>
      <label className="released-label" >Released: </label>
      <h4 className="platform-h3" >

        {gameDetail.released ? gameDetail.released : "No released found"}
      </h4>
      <label className="rating-label">Rating: </label>
      <h4 className="rating-h4">
         {gameDetail.rating ? gameDetail.rating : "No rating found"}
      </h4>
      <label className="description-label">Description: </label>
      <p className="description-p">

        {gameDetail.description
          ? gameDetail.description
          : "No description found"}
      </p>
    </div>
    </div>
  );
}





