import React from 'react'
import {Link, NavLink} from "react-router-dom"
import styles from "./Home.module.css"
import {useState, useEffect} from "react"//hooks
import Card from "./Card.js"
import Paginado from './Paginado.js'
import SearchBar from './SearchBar.js'

import { useDispatch, useSelector} from "react-redux"
//y estos son equivalentes a mapsStateToProps y mapsdispatchToProps
import { getVideogames, orderAlphabetic, orderRating,createVideogames, filterByGenre, filterCreated } from '../actions'


function Home() {
// este es como mapsdispatchToProps
  const dispatch = useDispatch();
  // aca con el useEffect despachas la accion a tu store o a tu reducer 

  //cada vez que lo invoco me trae todo el state
  const allGames= useSelector((state)=> state.videogames)


  // esto es equivalente a mapsStateToProps

  //state.pokemons es el [] dentro del state

  //este setea la pagina actual, que comienza en 1 y despues va cambiando a travez de setCurrentPage
  const[currentPage, setCurrentPage] = useState(1)
  const[pokemonsPerPage, setPokemonsPerPage] = useState(12)
  const[order, setOrder] = useState("")
  //este es un estado local que setea cuantos pokemons hay por pagina
 const indexLastPokemon = currentPage * pokemonsPerPage
 const indexFirstPokemon = indexLastPokemon - pokemonsPerPage

 const currentPokemons = allPokemons.slice(indexFirstPokemon,indexLastPokemon)


//aca ejecuto el setCurrentPage => set State
 const paginado = (pageNumber) =>{
 setCurrentPage(pageNumber)}

 //constante paginado es el que me va a ayudar en el renderizado


  //esto es equivalente a componentDidmount y componentDidUpdate
  // aca le pasamos como segundo paramentro un array vacio porque no depende de nadie, sino tendrias que indeicar de que depende este efecto, sino se estaria actualizando todo el tiempo
  useEffect (()=>{
    dispatch(getPokemons());
  },[dispatch])


 function handleClick(e){
   e.preventDefault();
   // esto es para que se resetee todo de vuelta! te traiga nuevamnete toso los pokemons
   dispatch(getPokemons())

 }

 function handleOrder(e){
  e.preventDefault();
  dispatch(orderAlphabetic(e.target.value))
  // esto es para que se resetee todo de vuelta! te traiga nuevamnete toso los pokemons
  setCurrentPage(1)
  //esto sirve para que me ordene, es decir comienza con un string vacio y luego se setea segun el target value
  setOrder(`Ordenado ${e.target.value}`)

}



 function handleChangeFilterType(e){
  dispatch(filterByType(e.target.value))

 }

 function handleChangeFilterCreated(e){
  dispatch(filterCreated(e.target.value))

 }
  return (

    <div className={styles.body}>
    <div className={styles.contenedor}>
    <div>
    <NavLink to="/" exact>
    <button className={styles.btn}>
        INICIO
      </button>
      </NavLink>
    </div>
    <div>
        <NavLink to = "/videogames" exact>CREA TUS JUEGOS</NavLink>
         {/* <h1>VIDEOGAMES</h1> */}
      <button onClick={e=>{handleClick(e)}}> Volver a cargar tus videojuegos</button>
        </div>
      </div>

        <select onChange={e=>handleOrder(e)}>
        <option value="all">Orden</option>
          <option value="asc">A-Z</option>
          <option value= "desc">Z-A</option>
          </select>
        <select>
        <option value="all">Default</option>
          <option value="ordenF">Fuertes</option>
           <option value= "descF">Debiles</option>
        </select>

        <select onChange={e=>handleChangeFilterType(e)}>
          <option value="All">Types</option>


          </select>
          <select onChange={e=>handleChangeFilterCreated(e)}> 
          <option value= "all">Todos</option>
          <option value= "exis">Existente</option>
          <option value= "creado">Nuevo</option>
          </select>


  <Paginado
  pokemonsPerPage={pokemonsPerPage}
  allPokemons={allPokemons.length} //para que me de un valor numerico
  paginado={paginado}
  />

  <SearchBar/>


{/* aca no vas a hacer el mapeo de allPokemons=> sino de la porcion de pokemons por pagina */}
  {currentPokemons.length > 0 ?
  currentPokemons.map((el) =>{
    return(
      <div key={el.id}>
      <Link to={"/home/" + el.id}>
    <Card name ={el.name} type= {el.types} image = {el.image}/>

    </Link>
    </div>
    )}
  ):<img src="data:image/png;base64,"/>
} 

    </div>
  )
}

export default Home
